mapper.select:
select o.ORDER_ID as orderId , o.ORDER_DATE as orderDate , o.SEND_USER_ID as sendUserId , o.RECV_USER_ID as recvUserId , o.ORDER_TYPE as orderType , o.ORDER_STATE as orderState , o.PAY_TYPE as payType , o.SEND_AMOUNT as sendAmount , o.AMOUNT as amount , o.RECV_AMOUNT as recvAmount , o.ORDER_SN as orderSn , o.TIMEOUT_RECV as timeoutRecv , o.TIMEOUT_PAY as timeoutPay , o.TIMEOUT_DOWN as timeoutDown , o.IS_API as isApi , o.PLAT_DATE as platDate , o.SERVICE_REMARK as serviceRemark , o.SEND_ACCOUNT_NO as sendAccountNo , o.SEND_ACCOUNT_NAME as sendAccountName , o.RECV_ACCOUNT_NO as recvAccountNo , o.RECV_ACCOUNT_NAME as recvAccountName , o.BANK_NAME as bankName , o.QRIMG_ID as qrimgId , o.NOTIFY_URL as notifyUrl , o.DATA_STATUS as dataStatus , o.MODIFY_TIME as modifyTime , o.CREATE_TIME as createTime , o.REMARK as remark , o.SEND_BANK_NAME as sendBankName , o.RECV_BANK_NAME as recvBankName , o.SEND_RATE as sendRate , o.RECV_RATE as recvRate , o.SEND_RATE_AMOUNT as sendRateAmount , o.RECV_RATE_AMOUNT as recvRateAmount, i.BASE64 as base64 from orders o left join img i on i.img_id=o.QRIMG_ID
and ORDER_TYPE = #{orderType}
and SEND_USER_ID = #{sendUserId}
and RECV_USER_ID = #{recvUserId}
and ORDER_STATE in
#{orderState}

select *
from orders
where (SEND_USER_ID = #{params.userId} or RECV_USER_ID = #{params.userId})

    and ORDER_TYPE = #{params.orderType}


    and ORDER_STATE = #{params.orderState}

order by CREATE_TIME desc

SELECT o.ORDER_ID orderId, o.ORDER_SN orderSn,o.SEND_USER_ID sendUserId,us.MOBILE sendUserMobile,ur.MOBILE recvUserMobile,o.RECV_USER_ID recvUserId, o.ORDER_TYPE orderType,o.ORDER_STATE orderState, o.IS_API isApi,o.PLAT_DATE platDate, o.CREATE_TIME createTime,o.TIMEOUT_DOWN timeoutDown,o.TIMEOUT_RECV timeoutRecv,o.TIMEOUT_PAY timeoutPay,SERVICE_REMARK serviceRemark, o.SEND_AMOUNT sendAmount,o.AMOUNT amount,o.RECV_AMOUNT recvAmount FROM orders o LEFT JOIN `user` us on us.USER_ID = o.SEND_USER_ID LEFT JOIN `user` ur on ur.USER_ID = o.RECV_USER_ID where o.DATA_STATUS='valid' AND ( (o.TIMEOUT_RECV is null or (o.ORDER_STATE=1 and o.TIMEOUT_RECV is not null and TIMESTAMPDIFF(SECOND,o.CREATE_TIME,now())>=o.TIMEOUT_RECV)) or (o.TIMEOUT_PAY is null or (o.ORDER_STATE=2 and o.TIMEOUT_PAY is not null and TIMESTAMPDIFF(SECOND,o.CREATE_TIME,now())>=o.TIMEOUT_PAY))
)
AND o.ORDER_TYPE IN #{item}
AND o.ORDER_STATE IN #{item}
AND o.ORDER_STATE NOT IN #{item}
ORDER BY o.create_time asc limit #{limit}

mapper.update:
update orders
set ORDER_STATE = #{state},REMARK = #{remark}
where ORDER_ID = #{orderId} and ORDER_STATE = 0
